// Alle Packlisten
const allPacklists = collections['8daee9d2-96f6-4706-8371-93b25a3e0865']?.['data'] || [];

// Ziel-Packlisten-ID
const targetPacklistId = variables['50707d25-8af7-4e30-95c4-10482c35c6ff'];

// Einheitensystem: "metric" oder "imperial"
const unit_system = collections[/* private_user */ '19acc274-e458-41e1-9444-8adfd9c89c81']?.['data']?.[0]?.['unit_system'] || 'metric';

// Packliste mit der Ziel-ID finden
const packlist = allPacklists.find(p => p.id === targetPacklistId) || {};
const temporaryItems = Array.isArray(packlist.temporary_items) ? packlist.temporary_items : [];

// Summe Gewicht in Gramm (weight × quantity)
const totalGrams = temporaryItems.reduce((sum, item) => {
  const weight = Number(item?.weight) || 0; // g
  const qty = Number(item?.quantity) || 1;
  return sum + (weight * qty);
}, 0);

// Ausgabe je nach Einheitensystem
if (unit_system === 'imperial') {
  const totalLb = totalGrams * 0.00220462; // g → lb
  return totalLb.toLocaleString('en-US', {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
  }) + ' lb';
}

if (totalGrams >= 1_000_000) {
  return (totalGrams / 1_000_000).toLocaleString('de-DE', {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
  }) + ' t';
} else if (totalGrams >= 1_000) {
  return (totalGrams / 1_000).toLocaleString('de-DE', {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
  }) + ' kg';
} else {
  return totalGrams.toLocaleString('de-DE') + ' g';
}

