// Datenquellen
const allItems = collections['0dce084f-b2ae-473b-b14e-3291d4cd1006']?.['data'] || [];
const allPacklists = collections['8daee9d2-96f6-4706-8371-93b25a3e0865']?.['data'] || [];
const targetPacklistId = variables['50707d25-8af7-4e30-95c4-10482c35c6ff'];

// Optional: quantity berücksichtigen?
const includeQuantity = true;

// Einheitensystem: "metric" (g/kg/t) oder "imperial" (lb)
const unit_system = collections[/* private_user */ '19acc274-e458-41e1-9444-8adfd9c89c81']?.['data']?.[0]?.['unit_system'] || 'metric';

// Packliste & ausgewählte Items holen
const packlist = allPacklists.find(p => p.id === targetPacklistId) || {};
const selectedItems = Array.isArray(packlist.items) ? packlist.items : [];
const selectedIds = selectedItems.map(it => it.id).filter(Boolean);

// Summe der Gewichte in Gramm
const totalGrams = allItems
  .filter(item => selectedIds.includes(item.id))
  .reduce((sum, item) => {
    const base = Number(item.weight) || 0;           // Gewicht in g aus Stammdaten
    const qty = includeQuantity
      ? (Number(selectedItems.find(si => si.id === item.id)?.quantity) || 1)
      : 1;
    return sum + base * qty;
  }, 0);

// Ausgabe nach Einheitensystem formatieren
if (unit_system === 'imperial') {
  // g → lb
  const totalLb = totalGrams * 0.00220462;
  return totalLb.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 }) + ' lb';
}

// metric: automatische Einheit g/kg/t
if (totalGrams >= 1_000_000) {
  return (totalGrams / 1_000_000).toLocaleString('de-DE', { minimumFractionDigits: 2, maximumFractionDigits: 2 }) + ' t';
} else if (totalGrams >= 1_000) {
  return (totalGrams / 1_000).toLocaleString('de-DE', { minimumFractionDigits: 2, maximumFractionDigits: 2 }) + ' kg';
} else {
  return totalGrams.toLocaleString('de-DE') + ' g';
}

