const packlist = allPacklists.find(p => p.id === targetPacklistId);
const temporaryItems = Array.isArray(packlist?.temporary_items) ? packlist.temporary_items : [];

// Summe der Preise (mit quantity)
const totalPrice = temporaryItems.reduce((sum, it) => {
  const price = Number(it?.price) || 0;
  const qty = Number(it?.quantity) || 1;
  return sum + (price * qty);
}, 0);

// Währung lesen und absichern
const currencyRaw = collections[/* private_user */ '19acc274-e458-41e1-9444-8adfd9c89c81']?.['data']?.[0]?.['currency'] || 'EUR';
