// Quellitems
const items =
  collections['0dce084f-b2ae-473b-b14e-3291d4cd1006']?.['data'] ?? [];

// Datensatz mit passender ID holen
const source =
  collections['8daee9d2-96f6-4706-8371-93b25a3e0865']?.['data'] ?? [];
const needle = variables['50707d25-8af7-4e30-95c4-10482c35c6ff'];

const record = wwFormulas.filterByKey(source, 'id', needle)?.[0] ?? null;

// packed_status sicher auslesen (kann fehlen, null oder kein Array sein)
const packedRaw = Array.isArray(record?.packed_status) ? record.packed_status : [];

// IDs robust extrahieren: Einträge können Objekte {id: ...}, Strings, Zahlen, null sein
const toId = (v) => (v && typeof v === 'object' && 'id' in v ? v.id : v);

const selectedIds = new Set(
  packedRaw
    .map(toId)                 // Objekt -> id, sonst Wert behalten
    .filter((id) => id != null) // null/undefined raus
    .map((id) => String(id).trim()) // als String normalisieren
);

// Items mit packed-Flag zurückgeben (IDs ebenfalls normalisieren)
const updatedItems = items.map((item) => ({
  ...item,
  packed: selectedIds.has(String(item?.id).trim()),
}));

return updatedItems;

const items = wwFormulas.filterByKey(
  collections['8daee9d2-96f6-4706-8371-93b25a3e0865']?.['data'],
  "id",
  variables['50707d25-8af7-4e30-95c4-10482c35c6ff']
)[0]?.temporary_items || [];

const packedIds = wwFormulas.filterByKey(
  collections['8daee9d2-96f6-4706-8371-93b25a3e0865']?.['data'],
  "id",
  variables['50707d25-8af7-4e30-95c4-10482c35c6ff']
)[0]?.packed_status || [];

// Markiere jedes Item mit packed true/false
const updatedItems = items.map(item => ({
  ...item,
  packed: packedIds.includes(item.id)
}));

// Sortiere: zuerst packed false, dann true – jeweils nach created_date absteigend
const sortedItems = updatedItems.sort((a, b) => {
  if (a.packed !== b.packed) {
    return a.packed - b.packed; // false zuerst
  }

  const dateA = new Date(a.created_date);
  const dateB = new Date(b.created_date);
  return dateB - dateA; // neueste zuerst
});

return sortedItems;

