// Datenquellen
const allItems = collections['0dce084f-b2ae-473b-b14e-3291d4cd1006']?.['data'] || [];
const allPacklists = collections['8daee9d2-96f6-4706-8371-93b25a3e0865']?.['data'] || [];
const targetPacklistId = variables['50707d25-8af7-4e30-95c4-10482c35c6ff'];

// Währungssystem ("EUR" oder "USD")
const currency_system = collections[/* private_user */ '19acc274-e458-41e1-9444-8adfd9c89c81']?.['data']?.[0]?.['currency'] || 'EUR';

// Packliste & ausgewählte Items
const packlist = allPacklists.find(p => p.id === targetPacklistId) || {};
const selectedItems = Array.isArray(packlist.items) ? packlist.items : [];

// Map: id -> quantity (Default 1)
const qtyById = new Map(
  selectedItems
    .filter(si => si && si.id)
    .map(si => [si.id, Number(si.quantity) > 0 ? Number(si.quantity) : 1])
);

// Summe Preis * Menge
let totalPrice = allItems.reduce((sum, item) => {
  if (!item?.id) return sum;
  const price = Number(item.purchase_price) || 0;
  const qty = qtyById.get(item.id) || 0;
  return sum + (price * qty);
}, 0);

// USD ohne Umrechnung
if (currency_system === 'USD') {
  return totalPrice.toLocaleString('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
  });
}

// Standard: EUR
return totalPrice.toLocaleString('de-DE', {
  style: 'currency',
  currency: 'EUR',
  minimumFractionDigits: 2,
  maximumFractionDigits: 2
});

