function convertMinutesToHoursAndMinutes(minutes) {
    if (minutes < 0) {
        return 'u.A.';
    }

    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;

    if (hours === 0) {
        return `${remainingMinutes} Min`;
    } else if (remainingMinutes === 0) {
        return `${hours} Std`;
    } else {
        return `${hours}Std ${remainingMinutes}Min`;
    }
}

// Example: Conversion of 90 minutes
const inputMinutes = context.item.data?.['shot_duration']
const converted = convertMinutesToHoursAndMinutes(inputMinutes);

return converted;

