// Sprache (falls du irgendwann Labels sprachabhängig brauchst)
const lang = (globalContext.page?.['lang'] || 'de').toLowerCase();

// Datenquelle
const data = collections['0dce084f-b2ae-473b-b14e-3291d4cd1006']?.['data'] || [];
const optionList = variables['c9c7c94b-20ec-4ace-86b2-efc24ef0ede5'] || [];

// Helfer zum Normalisieren
const norm = v => (v ?? '').toString().trim();

// 1) Alle eindeutigen Kategorien aus data
const distinctCats = Array.from(
  new Set(
    data
      .map(i => norm(i.category))
      .filter(c => c !== '')
  )
);

// 2) OptionList-Map für schnellen Zugriff
const optMap = new Map(optionList.map(o => [o.value, o]));

// 3) Arrays für Treffer und Extras
const results = [];
const extras  = [];

// a) Kategorien in OptionList-Reihenfolge übernehmen
for (const opt of optionList) {
  const cat = opt.value;
  if (distinctCats.includes(cat)) {
    results.push({
      value: cat,
      label_de: norm(opt.label_de) || cat,
      label_en: norm(opt.label_en) || cat
    });
  }
}

// b) Extras sammeln (nicht in OptionList enthalten)
for (const cat of distinctCats) {
  if (!optMap.has(cat)) {
    extras.push({
      value: cat,
      label_de: cat,
      label_en: cat
    });
  }
}

// 4) Finale Liste: erst OptionList-Reihenfolge, dann Extras
const finalList = [...results, ...extras];

return finalList;

