const equipment = collections['8daee9d2-96f6-4706-8371-93b25a3e0865']?.['data'] || [];
const currentPacklist = equipment.find(item => item.id == variables['50707d25-8af7-4e30-95c4-10482c35c6ff']) || {};

const selectedIds = currentPacklist.packed_status || [];

const allItems = [
  ...(currentPacklist.items || []),
  ...(currentPacklist.temporary_items || [])
];

// Nur gültige Items (mit gültiger quantity)
const validItems = allItems.filter(item => typeof item.quantity === 'number' && !isNaN(item.quantity));

// Gesamtmenge
const totalQuantity = validItems.reduce((acc, item) => acc + item.quantity, 0);

// Gepackte Menge
const packedQuantity = validItems
  .filter(item => selectedIds.includes(item.id))
  .reduce((acc, item) => acc + item.quantity, 0);

// Prozent berechnen
const percentagePacked = totalQuantity > 0
  ? Math.round((packedQuantity / totalQuantity) * 100)
  : 0;

return percentagePacked;

