const currentPacklist = equipment.find(item => item.id == context.item.data?.['id']) || {};

const selectedIds = currentPacklist.packed_status || [];

const filteredEquipment = currentPacklist.items || [];
const filteredTemporary = currentPacklist.temporary_items || [];

const sumFilteredEquipment = filteredEquipment
  .filter(item => typeof item.quantity === 'number' && !isNaN(item.quantity))
  .filter(item => selectedIds.includes(item.id))
  .reduce((acc, item) => acc + item.quantity, 0);

const sumFilteredTemporary = filteredTemporary
  .filter(item => typeof item.quantity === 'number' && !isNaN(item.quantity))
  .filter(item => selectedIds.includes(item.id))
  .reduce((acc, item) => acc + item.quantity, 0);

const finalResult = sumFilteredEquipment + sumFilteredTemporary;

return finalResult;

