function removeFileExtension(filename) {
  // Finde den letzten Punkt in der Dateinamenzeichenkette
  const lastDotIndex = filename.lastIndexOf(".");

  // Wenn ein Punkt gefunden wurde und er nicht am Anfang oder Ende steht
  if (lastDotIndex !== -1 && lastDotIndex !== 0 && lastDotIndex < filename.length - 1) {
    // Schneide alles ab dem Punkt ab, um die Dateiendung zu entfernen
    return filename.substring(0, lastDotIndex);
  }

  // Wenn kein Punkt gefunden wurde oder er am Anfang/Ende steht,
  // gib einfach den ursprünglichen Dateinamen zurück
  return filename;
}

// Beispielaufruf
const filename = context.parameters['originalFilename'];

const filenameWithoutExtension = removeFileExtension(filename);

return filenameWithoutExtension;

