function validateAndCorrectEmails(emailArray) {
    const isValidEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

    return emailArray
        .map(email => {
            // Trim whitespace and convert to lowercase for uniformity
            const trimmedEmail = email.trim().toLowerCase();

            // Replace common typos like " @ " or " . " with "@" and "."
            const correctedEmail = trimmedEmail
                .replace(/\s*@\s*/g, '@')
                .replace(/\s*\.\s*/g, '.');

            return correctedEmail;
        })
        .filter(isValidEmail); // Only keep valid emails
}

