function transformText(text) {
  // Ersetzen von Umlauten
  const umlautMap = {
    '√§': 'ae',
    '√∂': 'oe',
    '√º': 'ue',
    '√Ñ': 'Ae',
    '√ñ': 'Oe',
    '√ú': 'Ue',
    '√ü': 'ss'
  };
  
  text = text.replace(/[√§√∂√º√Ñ√ñ√ú√ü]/g, function(match) {
    return umlautMap[match];
  });

  // Entfernen von Sonderzeichen
  text = text.replace(/[^a-zA-Z0-9\s]/g, '');

  // Ersetzen von Leerzeichen durch Unterstriche
  text = text.replace(/\s+/g, '_');

  // Anf√ºgen von .pdf
  text = text + '.pdf';

  return text;
}

// Beispielverwendung
const originalText = context.workflow['ad82a1e2-bfa9-4aa4-82ab-9e1d7543f1ad'].result
const transformedText = transformText(originalText);
return transformedText;
