const element = event?.target;
if (!element) return;

const rect = element.getBoundingClientRect();

// absolute Position in px
const absX = event.clientX - rect.left;
const absY = event.clientY - rect.top;

// relative Position in %
const mouseX = Math.round((absX / rect.width) * 100);
const mouseY = Math.round((absY / rect.height) * 100);

return {
  mouseXPercent: mouseX,
  mouseYPercent: mouseY,
  mouseXPixel: Math.round(absX),
  mouseYPixel: Math.round(absY)
};

