// Funktion zur Formatierung von Minuten in "x Std. y Min."
function formatMinutes(minutes) {
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;

    let result = "";
    if (hours > 0) {
        result += `${hours} Std.`;
    }
    if (remainingMinutes > 0) {
        if (result) result += " ";
        result += `${remainingMinutes} Min.`;
    }
    return result || "0 Min.";
}

// Extrahiere und formatiere Minuten als Dauer
const duration = formatMinutes(
    wwFormulas.filterByKey(
        wwFormulas.filterByKey(
            collections['a92c1d6a-66a2-477f-9b9b-12d7a88616a1']?.['data'],
            "id",
            variables['e3c729bf-2226-489c-8f1d-99c0d598db48']
        )[0].linked_shots,
        "id",
        context.item.data?.['id']
    )[0].shot_duration
);

// Rckgabe der formatierten Dauer
return duration;

