function konvertiereMinutenZuStundenUndMinuten(minutenArray) {
    // berprfen, ob die Eingabe ein gltiges Array ist
    if (!Array.isArray(minutenArray) || minutenArray.some(min => min < 0)) {
        return 'Ungltige Eingabe';
    }

    // Alle Minuten in der Liste summieren
    const gesamtMinuten = minutenArray.reduce((gesamt, minuten) => gesamt + minuten, 0);

    // Minuten in Stunden und verbleibende Minuten umrechnen
    const stunden = Math.floor(gesamtMinuten / 60);
    const verbleibendeMinuten = gesamtMinuten % 60;

    // Ausgabe je nach berechneten Werten erstellen
    if (stunden === 0) {
        return `${verbleibendeMinuten} Min.`;
    } else if (verbleibendeMinuten === 0) {
        return `${stunden} Std.`;
    } else {
        return `${stunden} Std. ${verbleibendeMinuten} Min.`;
    }
}

try {
    // Extrahieren der Minutenwerte aus den verknpften Shots
    const minutenArray = wwFormulas.filterByKey(
            collections['a92c1d6a-66a2-477f-9b9b-12d7a88616a1']?.['data'],
            "id",
            variables['e3c729bf-2226-489c-8f1d-99c0d598db48']
        )[0].linked_shots.map(item => item['shot_duration']);

    // Sicherstellen, dass die Daten ein erwartetes Array mit Zahlen sind
    console.log(minutenArray);

    // Umrechnung der Minuten in Stunden und Minuten
    const ergebnis = konvertiereMinutenZuStundenUndMinuten(minutenArray);

    // Rckgabe des Ergebnisses
    return ergebnis;

} catch (fehler) {
    // Fehler abfangen und entsprechend behandeln
    console.error('Ein Fehler ist aufgetreten:', fehler);
    return 'Ein Fehler ist aufgetreten'; // Alternativ eine spezifischere Fehlermeldung zurckgeben
}

