function convertMinutesToHoursAndMinutes(minutesArray) {
    // Check if the input array is valid
    if (!Array.isArray(minutesArray) || minutesArray.some(min => min < 0)) {
        return 'Invalid input';
    }

    // Sum all the minutes in the array
    const totalMinutes = minutesArray.reduce((total, minutes) => total + minutes, 0);

    // Convert the total minutes to hours and remaining minutes
    const hours = Math.floor(totalMinutes / 60);
    const remainingMinutes = totalMinutes % 60;

    // Construct the output based on the calculated hours and minutes
    if (hours === 0) {
        return `${remainingMinutes} min`;
    } else if (remainingMinutes === 0) {
        return `${hours} hr`;
    } else {
        return `${hours} hr ${remainingMinutes} min`;
    }
}

try {
    // Extracting just the minutes from each item in the array
    const inputMinutesArray = wwFormulas.filterByKey(
        collections['a92c1d6a-66a2-477f-9b9b-12d7a88616a1']?.['data'],
        "id",
        variables['e3c729bf-2226-489c-8f1d-99c0d598db48']
    )[0].linked_shots.map(item => item['shot_duration']);

    // Ensure the array is what we expect, should be an array of numbers (minutes)
    console.log(inputMinutesArray);

    // Using the conversion function with the array of minutes
    const converted = convertMinutesToHoursAndMinutes(inputMinutesArray);

    // Returning the result of the conversion
    return converted;

} catch (error) {
    // Handle any errors that occur during the mapping, calculation, or any other process
    console.error('An error occurred:', error);
    return 'An error occurred'; // or return more specific error text
}

