function stripHTML(html) {
    var tempDiv = document.createElement("div");
    tempDiv.innerHTML = html;
    return tempDiv.textContent || tempDiv.innerText || "";
}

function shortenText(text) {
    const dummyText = "Empty note";

    if (text === null) {
        return dummyText;
    }

    if (text.length > 400) {
        return text.substring(0, 400) + "...";
    }

    return text;
}

// Example usage
const myHTMLText = context.item.data?.['scene_description'];
const plainText = stripHTML(myHTMLText);
const shortenedText = shortenText(plainText);

console.log(shortenedText);

return shortenedText;

