function filterObjectsByLinkedShot(objects, targetId) {
    if (!objects || !Array.isArray(objects)) {
        console.error("Invalid data: objects is not an array", objects);
        return [];
    }

    if (!targetId) {
        console.warn("No valid targetId provided, returning empty dataset.");
        return [];
    }

    // Nur Objekte zurckgeben, in denen targetId in linked_shots existiert
    return objects.filter(obj => 
        obj.linked_shots?.some(shot => shot.id === targetId)
    );
}

// Beispiel-Daten
