const originalPath = context.workflow['a5d5ced0-320c-4767-84b6-aff5e4f0fdaf'].loop?.['item']?.['s_thumbnail_path'];

// Steuerbarer Unix-Timestamp (hier kannst du ihn manuell setzen oder dynamisch generieren)
const customTimestamp = context.workflow['dc8dd764-17c9-4b25-a2d9-12bd88ae9df0'].result

// Funktion, die den gegebenen Timestamp und "_copy" vor dem letzten Punkt einfgt
function insertTimestampAndCopyBeforeExtension(filePath, timestamp) {
  if (!filePath) {
    return filePath; // Falls der Pfad nicht existiert, direkt zurckgeben
  }

  // Letzten Punkt im Dateinamen suchen
  const dotIndex = filePath.lastIndexOf('.');

  // Wenn kein Punkt gefunden wird, den Pfad unverndert zurckgeben
  if (dotIndex === -1) {
    return `${filePath}_${timestamp}_copy`;
  }

  // Timestamp und "_copy" direkt vor dem letzten Punkt einfgen
  return filePath.slice(0, dotIndex) + `_${timestamp}_copy` + filePath.slice(dotIndex);
}

// Anwendung der Funktion mit dem steuerbaren Timestamp
const newPath = insertTimestampAndCopyBeforeExtension(originalPath, customTimestamp);

return newPath;

