function filterObjectsByLinkedShot(objects, targetId) {
    if (!objects || !Array.isArray(objects)) {
        console.error("Invalid data: objects is not an array", objects);
        return false;
    }

    if (!targetId) {
        console.warn("No valid targetId provided, returning false.");
        return false;
    }

    // Prfen, ob mindestens ein passendes Objekt existiert
    return objects.some(obj => 
        obj.linked_shots?.some(shot => shot.id === targetId)
    );
}

