// 1. Hole die Daten aus dem Workflow
const files = context.workflow['f4c7602b-a4ee-41d3-a68e-5525698b6f64'].result;

// 2. Baue daraus das Kopier-Array
const timestamp = Date.now(); // Aktueller Timestamp

const filesToCopy = [];

files.forEach(file => {
  if (file.thumbnail_path) {
    const fullPath = file.thumbnail_path;
    const parts = fullPath.split('.');
    const extension = parts.pop();
    const pathWithoutExt = parts.join('.');

    filesToCopy.push({
      id: file.id,
      type: 'thumbnail',
      from: fullPath,
      to: `${pathWithoutExt}_${timestamp}_copy.${extension}`
    });
  }

  if (file.s_thumbnail_path) {
    const fullPath = file.s_thumbnail_path;
    const parts = fullPath.split('.');
    const extension = parts.pop();
    const pathWithoutExt = parts.join('.');

    filesToCopy.push({
      id: file.id,
      type: 's_thumbnail',
      from: fullPath,
      to: `${pathWithoutExt}_${timestamp}_copy.${extension}`
    });
  }
});

// 3. Funktion fr Copy in Batches (ohne Lschen!)
async function copyInBatches(filesToCopy, batchSize = 20) {
  const copiedResults = [];

  for (let i = 0; i < filesToCopy.length; i += batchSize) {
    const batch = filesToCopy.slice(i, i + batchSize);

    const results = await Promise.all(
      batch.map(async file => {
        const storage = wwLib.wwPlugins.supabase.instance.storage.from('user_data');

        const copyResult = await storage.copy(file.from, file.to);
        return { ...copyResult, id: file.id, type: file.type, newPath: file.to };
      })
    );

    results.forEach(result => {
      if (result.error) {
        console.error(` Fehler beim Kopieren:`, result.error);
      } else {
        console.log(` Erfolgreich kopiert: ${result.newPath}`);
        copiedResults.push({
          id: result.id,
          type: result.type,
          newPath: result.newPath
        });
      }
    });
  }

  return copiedResults;
}

// 4. Starte den Kopiervorgang
const copiedFiles = await copyInBatches(filesToCopy);

// 5. Gruppiere das Ergebnis nach ID
const groupedResult = {};

copiedFiles.forEach(item => {
  if (!groupedResult[item.id]) {
    groupedResult[item.id] = {};
  }

  if (item.type === 'thumbnail') {
    groupedResult[item.id].new_thumbnail_path = item.newPath;
  } else if (item.type === 's_thumbnail') {
    groupedResult[item.id].new_s_thumbnail_path = item.newPath;
  }
});

// 6. Endgltige Liste
const finalResult = Object.entries(groupedResult).map(([id, paths]) => ({
  id,
  ...paths
}));

return finalResult

