// Basis Array
const basisArray = collections[/* shotlist_all */ '3030eac5-a2fb-4073-9272-f9d63978e5f0']?.['data'];

// Positions Array
const positionsArray = context.item.data?.['linked_shots'];

// Funktion, die die Arrays verbindet und die Sortierung basierend auf dem Positions-Array bernimmt
const combineArrays = (basis, positions) => {
  return positions.map(pos => {
    // Finde das Basis-Item basierend auf der ID
    const item = basis.find(basisItem => basisItem.id === pos.id);
    // Wenn ein passendes Basis-Item gefunden wurde, fge es der Liste hinzu
    return item ? { ...item } : null;
  }).filter(item => item !== null);
};

// Verbinde die Arrays
const combinedArray = combineArrays(basisArray, positionsArray);

return combinedArray;

