const data = [...data1, ...data2];

data.sort((a, b) => {
  if (a.scene_number === null && b.scene_number === null) {
    return a.position_shotlist - b.position_shotlist; // Aufsteigend sortieren
  }
  if (a.scene_number === null) {
    return -1; // Null scene_number kommt zuerst
  }
  if (b.scene_number === null) {
    return 1; // Null scene_number kommt zuerst
  }
  if (a.scene_number !== b.scene_number) {
    return a.scene_number - b.scene_number; // Aufsteigend nach scene_number sortieren
  }
  return a.position_shotlist - b.position_shotlist; // Aufsteigend nach position_shotlist sortieren
});

