async function uploadFile(bucketName, filePath, db_fileName, file) {
    console.log('Starting upload process...');
    const {
        data: {
            session
        }
    } = await wwLib.wwPlugins.supabaseAuth.publicInstance.auth.getSession();
    console.log('Session retrieved:', session);

    return new Promise((resolve, reject) => {
        console.log('Creating tus upload...');
        var upload = new tus.Upload(file, {
            endpoint: `https://${projectId}.supabase.co/storage/v1/upload/resumable`,
            retryDelays: [0, 3000, 5000, 10000, 20000],
            headers: {
                authorization: `Bearer ${session.access_token}`,
                'x-upsert': 'true',
            },
            uploadDataDuringCreation: true,
            removeFingerprintOnSuccess: true,
            metadata: {
                bucketName: bucketName,
                objectName: `${filePath}/${db_fileName}`,
                contentType: file.type,
                cacheControl: '3600',
            },
            chunkSize: 6 * 1024 * 1024,
            onError: function(error) {
                console.log('Upload failed:', error);
                reject(error);
            },
            onProgress: function(bytesUploaded, bytesTotal) {
                var percentage = ((bytesUploaded / bytesTotal) * 100).toFixed(0);
                console.log('Upload progress:', percentage + '%');

                // Update the specific object's percentage in context variables if it exists
                if (variables['a815896e-81a6-422f-adce-b5133065fc3d'] !== undefined) {
                    var obj = variables['a815896e-81a6-422f-adce-b5133065fc3d'].find(o => o.id === objectId);
                    if (obj) {
                        obj.percentage = percentage;
                    } else {
                        console.warn('Object with the specified ID not found');
                    }
                } else {
                    console.warn('Variables object not found in context');
                }
            },
            onSuccess: function() {
                console.log('Download %s from %s', upload.file.name, upload.url);

                // Update the specific object's uploaded status in context variables if it exists
                if (variables['a815896e-81a6-422f-adce-b5133065fc3d'] !== undefined) {
                    var obj = variables['a815896e-81a6-422f-adce-b5133065fc3d'].find(o => o.id === objectId);
                    if (obj) {
                        obj.uploaded = true;
                        obj.percentage = "100";
                    } else {
                        console.warn('Object with the specified ID not found');
                    }
                } else {
                    console.warn('Variables object not found in context');
                }

                // Dieser Teil wird nach erfolgreichem Upload ausgefhrt
                wwLib.wwWorkflow.executeGlobal(`5fb503a0-4564-4414-ba7f-9ba7a4d0824c`, {
                    [`user_id`]: user_id,
                    [`element_name`]: file_name,
                    [`file_path`]: file_path,
                    [`file_size`]: file_size,
                    [`project_link`]: globalContext.pageParameters['ProjektUID'],
                    [`file_type`]: file_type,
                    [`full_file_type`]: full_file_type,
                    [`final_upload`]: final_upload
                });

                resolve();
            },
        });

        upload.findPreviousUploads().then(function(previousUploads) {
            console.log('Previous uploads found:', previousUploads);
            if (previousUploads.length) {
                upload.resumeFromPreviousUpload(previousUploads[0]);
            }
            console.log('Starting the upload...');
            upload.start();
        }).catch((error) => {
            console.log('Error finding previous uploads:', error);
            reject(error);
        });
    });
}

function startUpload() {
    console.log('Starting upload script...');
    const file = context.parameters['file'];
    if (!file) {
        console.error('No file found in context variables');
        return;
    }

    const filePath = context.workflow['ff93a2a5-a608-47b2-b335-f0915dd60429'].result;

    const bucketName = 'user_data';


    uploadFile(bucketName, filePath, db_fileName, file).then(() => {
        console.log('File uploaded successfully');
    }).catch((error) => {
        console.error('File upload failed', error);
    });
}

