const array1 = Array1; // z.B. [{ id: 5 }, { id: 2 }, { id: 9 }]
const array2 = Array2; // z.B. [{ id: 2, name: "B" }, { id: 5, name: "A" }, { id: 9, name: "C" }]

// Mapping der IDs zu ihrem Index in array1
const idOrderMap = new Map(array1.map((obj, index) => [obj.id, index]));

// Sortierung von array2 basierend auf array1
const sortedArray2 = array2.slice().sort((a, b) => {
  return (idOrderMap.get(a.id) ?? Infinity) - (idOrderMap.get(b.id) ?? Infinity);
});

return sortedArray2;
