function isItemAlreadyPacked(equipment_packing, itemId, newStart, newEnd) {
  const newStartDate = new Date(newStart);
  const newEndDate = new Date(newEnd);

  for (const packing of equipment_packing) {
    const packingStart = new Date(packing.date_range.start);
    const packingEnd = new Date(packing.date_range.end);

    const isOverlap = newStartDate <= packingEnd && newEndDate >= packingStart;
    const itemExists = Array.isArray(packing.items) && packing.items.includes(itemId);

    console.log(`Checking packing ${packing.id} → Overlap: ${isOverlap}, Item exists: ${itemExists}`);

    // Wenn beides zutrifft → Item ist belegt → true
    if (isOverlap && itemExists) {
      return true;
    }
  }

  return false; // In keinem overlapping Zeitraum enthalten → also verfügbar
}

