const mapping = context.mapping;
const allPacklists = collections['2277ac28-5e33-4a04-808c-08a786f04d01']?.['data'] || [];
const allItemDefinitions = collections['9e64243e-31d3-45aa-a10b-74d402081c80']?.['data'] || [];

// ⬅️ Hilfsfunktion für Zeitraumprüfung
function isOverlapping(aStart, aEnd, bStart, bEnd) {
  return new Date(aStart) <= new Date(bEnd) && new Date(bStart) <= new Date(aEnd);
}

// ⬅️ Lookup: itemId → max verfügbare Menge
const itemQuantities = Object.fromEntries(
  allItemDefinitions.map(item => [item.id, item.quantity ?? 1])
);

// ⬅️ Zähle Nutzung eines Items in einer Packliste
function getItemUsage(packingList) {
  const usage = {};

  for (const entry of packingList.items_quantity || []) {
    const id = entry.id;
    const qty = entry.quantity || 1;
    usage[id] = (usage[id] || 0) + qty;
  }

  return usage;
}

// Hauptfunktion: prüfe Konflikte
function checkConflict(current, allOthers) {
  const { start, end } = current.date_range || {};
  if (!start || !end) return 'unbekannt';

  const currentUsage = getItemUsage(current);

  for (const other of allOthers) {
    if (other.id === current.id) continue;

    const otherStart = other.date_range?.start;
    const otherEnd = other.date_range?.end;
    if (!otherStart || !otherEnd) continue;

    if (!isOverlapping(start, end, otherStart, otherEnd)) continue;

    const otherUsage = getItemUsage(other);

    for (const itemId in currentUsage) {
      const totalBooked =
        (currentUsage[itemId] || 0) + (otherUsage[itemId] || 0);
      const maxAvailable = itemQuantities[itemId] || 1;

      if (totalBooked > maxAvailable) {
        return 'red';
      }
    }
  }

  return 'blue';
}

const farbe = checkConflict(mapping, allPacklists);
return farbe

